package week1.day1;

public class Learncondstmts {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=1;i<=50;i++)
			if (i%2==0)
			{
				System.out.println("The number "+ i + " is an even no");
				
			}
			else
			{
				System.out.println("The number "+i+" is a odd no");
			}

	}

}
